targets.query:
from(bucket: "${Bucket}")
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) =>
    r._measurement == "memory" and
    r._field == "memused"
  )
  |> filter(fn: (r) => r["host"] == "${server}")
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
  |> map(fn: (r) => ({
        r with
        _value: float(v: r._value) / 1073741824.0
      })
    )
  |> last()

from(bucket: "${Bucket}")
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) =>
    r._measurement == "cpustat" and
    r._field == "cpu"
  )
  |> filter(fn: (r) => r["host"] =~ /${server:regex}/)
  |> map(fn: (r) => ({
        r with
        _time: r._time,
        _value: float(v: r._value) * float(v: 100)
      })
    )
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)

from(bucket: "${Bucket}")
  |> range(start: v.timeRangeStart, stop:v.timeRangeStop)
  |> filter(fn: (r) =>
    r._measurement == "cpustat" and
    r._field == "wait"
  )
  |> filter(fn: (r) => r["host"] =~ /${server:regex}/)
  |> map(fn: (r) => ({
        r with
        _time: r._time,
        _value: float(v: r._value)
      })
    )
  |> aggregateWindow(every: 1m, fn: mean, createEmpty: false)
